(function(){"use strict";
/*!
   * angular filters about datetime
   * 
   * @required: moment.js
   */
angular.module("bnFilters",[]).filter("appDate",function(){return function(n){return moment(n).isValid()?moment.locale()==="zh-cn"?moment(n).format("YYYY-M-D"):moment(n).format("l"):""}}).filter("appDateTime",function(){return function(n){return moment(n).isValid()?moment.locale()==="zh-cn"?moment(n).format("YYYY-M-D HH:mm:ss"):moment(n).format("lll"):""}}).filter("appFullDateTime",function(){return function(n){return moment(n).isValid()?(moment.locale()==="zh-cn",moment(n).format("llll")):""}})}).call(this);